import React, { useState, useEffect, useRef } from 'react'
import './productShowcase.scss'
import shoes from './shoes.png'
import { v4 as uuidv4 } from 'uuid'
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCartSharp';

const ProductShowcase = () => {
    const [Products, SetProducts] = useState([]);
    const inputBackNext = useRef()
    useEffect(() => {
        fetch('http://localhost:9000/', {
            method: 'GET',
            headers: { 'Content-Type': 'application/json' }
        }).then(response => response.json().then(data => {
            SetProducts(data)
        }))
    }, [])
    function goBack() {
        console.log(inputBackNext.current)
    }
    function goNext() {
        console.log('Next')
    }

    return (
        <div className='productShowcase'>
            <div className='slides' ref={inputBackNext}>
                <div className='divBack'><button className='back' onClick={goBack} >Back</button></div>
                <div className='divNext'><button className='next' onClick={goNext}>Next</button></div>
                <div className='productCard'>
                    {Products.map(product => (
                        <div className='card' key={uuidv4()}>
                            <h2>{product.productName}</h2>
                            <h3>Current price: {product.productPrice}$</h3>
                            <img src={shoes} alt="" />
                            <div className='blackOverlay'>
                                <p>Add to cart</p>
                                <button><AddShoppingCartIcon /></button>

                            </div>
                        </div>

                    ))}
                </div>
            </div>
        </div>
    )
}

export default ProductShowcase
