import React, { useState, useRef } from 'react'
import './login.scss'

const Login = () => {
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [confirmPassword, setconfirmPassword] = useState('')
  const [email, setEmail] = useState('')

  let data = {
    username: username,
    email: email,
    password: password,
    confirmPassword: confirmPassword
  }

  function sendData() {
    fetch('http://localhost:9000/users', {
      method: 'POST',
      body: JSON.stringify(data),
      headers: { 'Content-Type': 'application/json' }
    }).then(response => {
      response.json().then(data => {
        if (data.success) {
          // do smth
          ispisi(data)
        }
      })
    })

  }
  const handleSubmit = (e) => {
    e.preventDefault();

  }
  function ispisi(data) {
    if (data.usernameFailed) {
        refUsername.current.classList.remove('d-none')
    }
    else {
        refUsernameSame.current.classList.add('d-none')
    }
    if (data.usernameLengthFailed) {
        document.querySelector('.pUsername').classList.remove('d-none')
    }
    else {
        document.querySelector('.pUsername').classList.add('d-none')
    }
    if (data.emailFailed) {
        document.querySelector('.pEmail').classList.remove('d-none')
    }
    else {
        document.querySelector('.pEmail').classList.add('d-none')
    }
    if (data.passwordLengthFailed) {
        document.querySelector('.pPassword').classList.remove('d-none')
    }
    else {
        document.querySelector('.pPassword').classList.add('d-none')
    }
    if (data.confirmPasswordFailed) {
        document.querySelector('.pPasswordConfirm').classList.remove('d-none')
    }
    else {
        document.querySelector('.pPasswordConfirm').classList.add('d-none')
    }

}

  return (
    <form className='login' onSubmit={handleSubmit}>
      <h2>Sign up</h2>
      <input type="text" placeholder="Username" onChange={e => setUsername(e.target.value)} />
      <p className="d-none" ref = {refUsername} >Username must have more than 6 Characters</p>
      <p className="d-none" ref={refUsernameSame}>Account with that username already exists</p>
      <input type="email" placeholder="Email address" onChange={e => setEmail(e.target.value)}></input>
      <p className="d-none" ref={refEmail}>Email address must be valid</p>
      <input type="password" placeholder="Password" onChange={e => setPassword(e.target.value)}></input>
      <p className="d-none" ref={refPassword}>Password must have atleast 6 Characters</p>
      <input type="password" placeholder="Confirm Password" onChange={e => setconfirmPassword(e.target.value)}></input>
      <p className="d-none" ref={refPasswordSame}>Passwords must be the same</p>
      <button onClick={sendData}>Sign up</button>
      <a href="/login">Already have an account? Sign in</a>
      <a href="/">Back</a>
    </form>
  )
}

export default Login
