  const location = useLocation();
  console.log(location.state.id)
  return (
      <>
      <Navbar cartNumber={props.cartNumber} setCartNumber={props.setCartNumber}/>
    <div>ProductInfo</div>
    </>
  )
}

export default ProductInfo
